body.script:
$(function() {
if(isHighDensity()) {
$.getScript( "/odoocms_web/static/assets/js/custom/dense.min.js", function(data) {
// enable hires images
altair_helpers.retina_images();
});
}
});
$window.on('load', function() {
// ie fixes
altair_helpers.ie_fix();
});

