body.script:
$(function() {
// datatables
altair_datatables.dt_scroll();

});
altair_datatables = {
dt_scroll: function() {
var $dt_scroll = $('#dt_scroll');
if($dt_scroll.length) {
$dt_scroll.DataTable({
"pageLength": 50,
"scrollY": "800px",
"scrollCollapse": false,
"paging": true
});
}
},};

$('label').click(function () {
var checked = $('input', this).is(':checked');
var reason = $('#student_reason_'+this.dataset['attendance_line']);
var came_late = $('#came_late_'+this.dataset['attendance_line']);
var left_early = $('#left_early_'+this.dataset['attendance_line']);

$('span', this).text(checked ? 'P' : 'A');
if(checked == true){
console.log("I am true");

$('span', this).removeClass('md-btn md-btn-danger md-btn-mini');
$('span', this).addClass('md-btn md-btn-success md-btn-mini');
$('input', this).val("True");

reason.val('');
reason.prop("disabled", true);

came_late.removeAttr("disabled");
left_early.removeAttr("disabled");
}
else{
console.log("I am false");
$('span', this).removeClass('md-btn md-btn-success md-btn-mini');
$('span', this).addClass('md-btn md-btn-danger md-btn-mini');
$('input', this).val("False");

reason.prop("disabled", false);

reason.val('02').attr("selected", "selected");
came_late.attr("disabled", 'true');
left_early.attr("disabled", 'true');
}
});
