$(document).ready(function() {
$("input[type=number]").on("focus", function() {
$(this).on("keydown", function(event) {
if (event.keyCode === 38 || event.keyCode === 40) {
event.preventDefault();
}
});
});

});
var blur = false;
$('table').on('blur', 'td select', function() {
if (!blur) {
blur = true;
var cell = $(this).closest('td');
table.cell(cell).data('example data');
blur = false;
}
});
$(document).ready(function () {

$('input').keyup(function (e) {
if (e.which == 39) { // right arrow
$(this).closest('td').next().find('input').focus();

} else if (e.which == 37) { // left arrow
$(this).closest('td').prev().find('input').focus();

} else if (e.which == 40) { // down arrow
$(this).closest('tr').next().find('td:eq(' + $(this).closest('td').index() +
')').find('input').focus();

} else if (e.which == 38) { // up arrow
$(this).closest('tr').prev().find('td:eq(' + $(this).closest('td').index() +
')').find('input').focus();
}
});

// un-comment to display key code
// $("input").keydown(function (e) {
// console.log(e.which);
// });
});

$(function() {
if(isHighDensity()) {
